import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles((theme) => ({
  mainCard: {
    position: "relative",
    backgroundColor: theme.palette.grey[800],
    color: theme.palette.common.white,
    marginBottom: theme.spacing(4),
    //backgroundImage: 'url(https://source.unsplash.com/random)',
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
  },
  overlay: {
    position: "absolute",
    top: 0,
    bottom: 0,
    right: 0,
    left: 0,
    backgroundColor: "rgba(0,0,0,.3)",
  },
  mainCardContent: {
    position: "relative",
    padding: theme.spacing(3),
    [theme.breakpoints.up("md")]: {
      padding: theme.spacing(6),
      paddingRight: 0,
    },
  },
}));

export default function MainCard(props) {
  const classes = useStyles();

  return (
    <Paper className={classes.mainCard}>
      {/* Increase the priority of the hero background image */}
      {/* {<img style={{ display: 'none' }} src={post.image} alt={post.imageText} />} */}
      <div className={classes.overlay} />
      <Grid container>
        <Grid item md={6}>
          <div className={classes.mainCardContent}>
            <Typography variant="h3" color="inherit" gutterBottom>
              {props.card.title}
              {/* Simple and fun way to start your cooking journey */}
            </Typography>
            <Typography variant="h5" color="inherit" paragraph>
              {props.card.description}
              <Typography variant="h5" color="inherit" paragraph>
                {props.card.description2}
              </Typography>
              {/* Love to cook but always struggle to come up with ideas? SmartCook
              is a web app designed for all food lovers who enjoy cooking,
              exploring new recipes, creating recipe journals, and socializing
              about food/cooking. */}
            </Typography>
          </div>
        </Grid>
      </Grid>
    </Paper>
  );
}
