    description:
      "Track all available ingredients in your pantry and fridge with ease",
  },
  {
    heading: "Track",
    title: "Ingredient Inventory",
    description:
      "Track all available ingredients in your pantry and fridge with ease",
  },
];

export default function LandingPage(props) {
  //const nums = Array.from(Array(4).keys());

  return (
    <div>
      <div className="landing-page-container">{/* <Header /> */}</div>
      <Header />
      <MainCard card={mainCardContent} />
      <GridList cols={4}>
        {gridCardContent.map((item) => {
          return <GridPost key={item.heading} content={item}></GridPost>;
        })}
      </GridList>
    </div>
  );
}
