export const addingIngredient= amount => {
	return {
		type: 'ADD_INGREDIENT',
		addingMessage: amount
	};
};


export const clearIngredients = amount => {
	return {
		type: 'CLEAR_INGREDIENT',
		addingMessage: amount
	};
};
