import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles((theme) => ({
  mainCard: {
    position: "relative",
    backgroundColor: theme.palette.grey[800],
    color: theme.palette.common.white,
    marginBottom: theme.spacing(4),
    //backgroundImage: 'url(https://source.unsplash.com/random)',
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    height: 320,
  },
  mainCardTitle: {
    paddingBottom: 15,
  },
  overlay: {
    position: "absolute",
    top: 0,
    bottom: 0,
    right: 0,
    left: 0,
    backgroundColor: "rgba(0,0,0,.3)",
  },
  mainCardContent: {
    position: "relative",
    padding: theme.spacing(3),
    [theme.breakpoints.up("md")]: {
      padding: theme.spacing(6),
      paddingRight: 0,
    },
  },
}));

export default function MainCard(props) {
  const classes = useStyles();

  return (
    <Paper
      className={classes.mainCard}
      style={{ backgroundImage: `url(${props.card.image})` }}
    >
      {<img style={{ display: "none" }} src={props.card.image} />}
      <div className={classes.overlay} />
      <Grid container>
        <Grid item md={7}>
          <div className={classes.mainCardContent}>
            {props.isAuthenticated ? (
              <div className={classes.mainCardTitleContainer}>
                {props.isAuthenticated}
                <Typography
                  className={classes.mainCardTitle}
                  variant="h4"
                  color="inherit"
                  gutterBottom
                >
                  {props.card.title_isAuthenticated}
                </Typography>
                <Typography variant="h6" color="inherit" paragraph>
                  {props.card.description_isAuthenticated}
                </Typography>
              </div>
            ) : (
              <div className={classes.mainCardTitleContainer}>
                {props.isAuthenticated}
                <Typography
                  className={classes.mainCardTitle}
                  variant="h4"
                  color="inherit"
                  gutterBottom
                >
                  {props.card.title}
                </Typography>
                <Typography variant="h6" color="inherit" paragraph>
                  {props.card.description}
                </Typography>
                {/* <Typography variant="h6" color="inherit" paragraph>
                  {props.card.description2}
                </Typography> */}
              </div>
            )}
          </div>
        </Grid>
      </Grid>
    </Paper>
  );
}
